import { useState } from "react";
import { useEffect } from "react";

function Waiting() {

    const [periods, setPeriods] = useState("");
    useEffect(() => {
        setTimeout(() => {
            setPeriods((periods) => periods + ".");
        }, 1000);
      });
    return(
    <div className="App">
        <div className="waitingPanel">
            <h1>Waiting {periods}</h1>

        </div>

    </div>
    )
}

export default Waiting;
