const Home = () =>{

    let joinRoomAff = async() =>{
        fetch('http://127.0.0.1:8000/api/join',{
          method: "PUT",
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({"side" : "aff"})

        })
    }
    let joinRoomNeg = async() =>{
        fetch('http://127.0.0.1:8000/api/join',{
            method: "PUT",
            headers: {
            'Content-Type': 'application/json'
            },
            body: JSON.stringify({"side" : "neg"})
        })
    }
    const roomName = "OMGSSCSR"

    const chatSocket = new WebSocket(
    'ws://'
    + "127.0.0.1:8000"
    + '/ws/chat/'
    + roomName
    + '/'
    )

    chatSocket.onmessage = function(e) {
        console.log("message recieved")
        console.log(e.data)
    }
    chatSocket.onclose = function(e) {
        console.log('Chat socket closed unexpectedly')
    }

    let sendMessage = () => {
        const messageInputDom = document.querySelector('#chat-message-input');
        const message = messageInputDom.value;
        chatSocket.send(JSON.stringify({
            'message': message
        }));
        messageInputDom.value = '';
    };

    return (
        <div className="App">
            <br/>
            <br/>
            <button onClick={joinRoomAff}>AFFIRMATIVE</button>
            <button onClick={joinRoomNeg}>NEGATIVE</button><br/><br/><br/>
            <input id="chat-message-input" type="text" size="100"></input><br/>
            <button onClick={sendMessage}>SUBMIT</button><br></br>



        </div>
    );
}

export default Home;
