import * as React from 'react'
import { useParams } from 'react-router-dom'
import { useState } from "react";
import { useEffect } from "react";
import { useRef } from "react";
import App from '../App';
import { useNavigate } from "react-router-dom";


function Message(props){
    return <li></li>
}

const Room = () =>{


    const { roomName } = useParams()
    const { side } = useParams()
    const [roomFull, setRoomFull] = useState(false)

    const chatSocket = useRef(null);

    const messages = useRef(new Array())


    const navigate = useNavigate();
    useEffect(() => {
        chatSocket.current = new WebSocket(
            'ws://'
            + "127.0.0.1:8000"
            + '/ws/chat/'
            + roomName
            + '/'
        )


        chatSocket.current.onmessage = function(e) {
            console.log("message recieved")
            let data = JSON.parse(e.data)
            if (data["type"] == "server"){

                if (data["message"]["content"] == "room_full_status" && data["message"]["status"]){
                    setRoomFull(true)
                }
                else if (data["message"]["content"] == "DISCONNECT"){
                    navigate("/")
                }
            }
            else{
                messages.current.push(data["message"])
                console.log(messages.current)
            }
        }
        chatSocket.current.onclose = function(e) {
            console.log('Chat socket closed unexpectedly')
            navigate("/")
        }
    }, []);

    let sendMessage = () => {
        const messageInputDom = document.querySelector('#chat-message-input');
        const message = messageInputDom.value;
        chatSocket.current.send(JSON.stringify({
            'message': message
        }));
        messageInputDom.value = '';
    };

    if (roomFull){
    return (

        <div className="App">


            <h1>{roomName} : {side}</h1>

            <input id="chat-message-input" type="text" size="100"></input><br/>
            <button onClick={sendMessage}>SUBMIT</button><br></br>


        </div>


    )    
    }
    else{
        return (
        <div className="App"> 
            <h1>waiting...</h1>
        </div>)
    }
}   

export default Room;
